                    <h1 class="auth-title" style="color: #f3516e;">Forgot Password</h1>
                    <p class="auth-subtitle mb-5">Input your email and we will send you reset password link.</p>

                    <form action="index.html">
                        <div class="form-group position-relative has-icon-left mb-4">
                            <input type="email" class="form-control form-control-xl inputMail" placeholder="Email">
                            <div class="form-control-icon">
                                <i class="bi bi-envelope"></i>
                            </div>
                        </div>
                        <button class="btn btn-primary btn-block btn-lg shadow-lg mt-5" style="filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25)); background: linear-gradient(121.18deg, #f3516e 6.51%, #a30a25 85.03%); border: 0;" sendrequest>Send</button>
                    </form>
                    <div class="text-center mt-5 text-lg fs-4">
                        <p class='text-gray-600'>Remember your account? <a href="/" class="font-bold" style="color: #f3516e;">Log in</a>.
                        </p>
                    </div>
                </div>
            </div>
        </div>

    </div>
    <script src="/adminpanel/assets/extensions/jquery/jquery.min.js"></script>
    <script src="//cdn.jsdelivr.net/npm/sweetalert2@11"></script>
    <script def>
        $("[sendrequest]").click(function (param) {  
            $(this).prop("disabled", true);
            var date = {
                "handler_id": "resetPassword",
                "mail": $(".inputMail").val(),
            };
            $.ajax({
                type: 'post',
                url: '/handler.php',
                data: date,
                success: function(result) {
                    var json = JSON.parse(result);
                    switch (json.type) {
                        case 1:
                            Swal.fire({
                                title: 'Пароль сброшен!',
                                icon: 'success',
                                text: "Пароль сброшен, на почту отправили временный пароль. Проверьте папку спам на почте"
                            })
                            break;
                        default:
                            Swal.fire({
                                title: 'Error!',
                                icon: 'error',
                                text: json.msg
                            })

                            break;
                    }
                }
            });
        });
        
    </script>
